	private static int testaDijkstra(String rotaHopfield, double[][] C)
			throws Exception {

		ShortestPathDijkstra dk = new ShortestPathDijkstra(5, 1, 3, C);

		if (rotaHopfield.equals(dk.getRotaDijkstra()))
			return 1;
		else {
			System.out.println("H(" + rotaHopfield + "); D("
					+ dk.getRotaDijkstra() + ")");
			return 0;
		}
	}

